import{_ as e,o as i,c as a,a2 as o}from"./chunks/framework.D1m2_e98.js";const u=JSON.parse('{"title":"Features","description":"","frontmatter":{"title":"Features"},"headers":[],"relativePath":"guide/features.md","filePath":"guide/features.md"}'),n={name:"guide/features.md"};function s(l,t,r,d,c,p){return i(),a("div",null,[...t[0]||(t[0]=[o(`<h1 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h1><h2 id="ini-parsing" tabindex="-1">INI Parsing <a class="header-anchor" href="#ini-parsing" aria-label="Permalink to &quot;INI Parsing&quot;">​</a></h2><ul><li>Load INI data from a physical file or from an in-memory character string</li><li>Automatic detection of all sections — no need to declare section names upfront</li><li>Automatic detection of all options within each section</li><li>Multi-line option values (continuation lines without <code>=</code> are joined to the previous option)</li><li>Inline comments stripped automatically (delimited by <code>;</code>)</li><li>Configurable option separator (default <code>=</code>, can be set to <code>:</code> or any single character)</li></ul><h2 id="ini-generation" tabindex="-1">INI Generation <a class="header-anchor" href="#ini-generation" aria-label="Permalink to &quot;INI Generation&quot;">​</a></h2><ul><li>Add sections and options programmatically with <code>add</code></li><li>Set option values of any intrinsic type (integer, real, logical, character) and all PENF kinds</li><li>Array-valued options with optional delimiter</li><li>Remove sections and options with <code>del</code></li><li>Save to a named file with <code>save</code></li></ul><h2 id="introspection" tabindex="-1">Introspection <a class="header-anchor" href="#introspection" aria-label="Permalink to &quot;Introspection&quot;">​</a></h2><ul><li>Check whether a section exists: <code>has_section</code></li><li>Check whether an option exists (globally or within a section): <code>has_option</code></li><li>Get a section name by index: <code>section</code></li><li>Get an option name by index: <code>option</code> (not yet in public API — use <code>loop</code>)</li><li>Get the index of a named section or option: <code>index</code></li><li>Count the number of space-separated values in an option: <code>count_values</code></li><li>Retrieve all option name/value pairs as a 2-D character array: <code>items</code></li><li>Iterate over all options (or options of one section) with a <code>while</code> loop: <code>loop</code></li><li>Pretty-print the full file contents to any Fortran unit: <code>print</code></li></ul><h2 id="ini-format-details" tabindex="-1">INI Format Details <a class="header-anchor" href="#ini-format-details" aria-label="Permalink to &quot;INI Format Details&quot;">​</a></h2><p>FiNeR follows the common INI conventions:</p><table tabindex="0"><thead><tr><th>Element</th><th>Syntax</th></tr></thead><tbody><tr><td>Section header</td><td><code>[section-name]</code></td></tr><tr><td>Option</td><td><code>key = value</code></td></tr><tr><td>Comment line</td><td><code>!</code>, <code>;</code>, or <code>#</code> as first non-blank character</td></tr><tr><td>Inline comment</td><td><code>;</code> followed by comment text</td></tr><tr><td>Multi-line value</td><td>continuation line has no <code>=</code>; it is appended to the previous option</td></tr><tr><td>Default separator</td><td><code>=</code> (configurable)</td></tr></tbody></table><p>Example:</p><div class="language-ini vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">; database settings</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">[database]</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">host</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> = localhost</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">port</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> = 5432</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">tags</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> = alpha beta gamma</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">[logging]</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">level</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> = info</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">! end of file</span></span></code></pre></div><h2 id="compiler-support" tabindex="-1">Compiler Support <a class="header-anchor" href="#compiler-support" aria-label="Permalink to &quot;Compiler Support&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Compiler</th><th>Status</th></tr></thead><tbody><tr><td>GNU gfortran ≥ 5.3</td><td>Supported</td></tr><tr><td>Intel Fortran ≥ 16.x</td><td>Supported</td></tr><tr><td>IBM XL Fortran</td><td>Not tested</td></tr><tr><td>g95</td><td>Not tested</td></tr><tr><td>NAG Fortran</td><td>Not tested</td></tr><tr><td>PGI / NVIDIA</td><td>Not tested</td></tr></tbody></table><h2 id="design-principles" tabindex="-1">Design Principles <a class="header-anchor" href="#design-principles" aria-label="Permalink to &quot;Design Principles&quot;">​</a></h2><ul><li><strong>Pure Fortran</strong> — no C extensions, no system calls beyond standard I/O</li><li><strong>OOP</strong> — all functionality exposed as type-bound procedures on <code>file_ini</code></li><li><strong>TDD</strong> — every public method is exercised by automated tests in <code>src/tests/</code></li><li><strong>KISS</strong> — simple, focused API without unnecessary abstractions</li><li><strong>Free &amp; Open Source</strong> — multi-licensed for both FOSS and commercial use</li></ul>`,16)])])}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
